import { Component, OnInit } from '@angular/core';
import { FuncionarioService } from '../../services/funcionario.service';
import { Funcionario } from '../../models/funcionario.model';

@Component({
  selector: 'app-funcionarios-list',
  templateUrl: './funcionarios-list.component.html',
  styleUrls: ['./funcionarios-list.component.css']
})
export class FuncionariosListComponent implements OnInit {
  funcionarios: Funcionario[] = [];
  salarioMedio: number = 0;
  loading = false;
  error = '';
  showForm = false;
  editingFuncionario: Funcionario | null = null;

  constructor(private funcionarioService: FuncionarioService) { }

  ngOnInit(): void {
    this.loadFuncionarios();
    this.loadSalarioMedio();
  }

  loadFuncionarios(): void {
    this.loading = true;
    this.funcionarioService.getFuncionarios().subscribe({
      next: (data) => {
        this.funcionarios = data;
        this.loading = false;
      },
      error: (error) => {
        this.error = 'Erro ao carregar funcionários: ' + error.message;
        this.loading = false;
      }
    });
  }

  loadSalarioMedio(): void {
    this.funcionarioService.getSalarioMedio().subscribe({
      next: (data) => {
        this.salarioMedio = data;
      },
      error: (error) => {
        console.error('Erro ao carregar salário médio:', error);
      }
    });
  }

  showNewForm(): void {
    this.editingFuncionario = null;
    this.showForm = true;
  }

  editFuncionario(funcionario: Funcionario): void {
    this.editingFuncionario = { ...funcionario };
    this.showForm = true;
  }

  deleteFuncionario(id: number): void {
    if (confirm('Deseja realmente excluir este funcionário?')) {
      this.funcionarioService.deleteFuncionario(id).subscribe({
        next: () => {
          this.loadFuncionarios();
          this.loadSalarioMedio();
        },
        error: (error) => {
          this.error = 'Erro ao excluir funcionário: ' + error.message;
        }
      });
    }
  }

  onFormSubmitted(): void {
    this.showForm = false;
    this.editingFuncionario = null;
    this.loadFuncionarios();
    this.loadSalarioMedio();
  }

  onFormCancelled(): void {
    this.showForm = false;
    this.editingFuncionario = null;
  }

  downloadRelatorio(): void {
    this.funcionarioService.getRelatorioPdf().subscribe({
      next: (blob) => {
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = 'relatorio-funcionarios.pdf';
        document.body.appendChild(a);
        a.click();
        document.body.removeChild(a);
        window.URL.revokeObjectURL(url);
      },
      error: (error) => {
        this.error = 'Erro ao baixar relatório: ' + error.message;
      }
    });
  }
}

<div class="container mt-4">
  <div class="row">
    <div class="col-12">
      <h2>Gerenciamento de Funcionários</h2>

      <div class="d-flex justify-content-between align-items-center mb-3">
        <div>
          <button class="btn btn-primary" (click)="showNewForm()" [disabled]="showForm">
            Novo Funcionário
          </button>
          <button class="btn btn-success" (click)="downloadRelatorio()">
            Gerar Relatório PDF
          </button>
        </div>
        <div class="alert alert-info mb-0" *ngIf="salarioMedio > 0">
          Salário Médio: {{ salarioMedio | currency:'BRL':'symbol':'1.2-2' }}
        </div>
      </div>

      <div class="alert alert-danger" *ngIf="error">
        {{ error }}
      </div>

      <div *ngIf="loading" class="text-center">
        <div class="spinner-border" role="status">
          <span class="visually-hidden">Carregando...</span>
        </div>
      </div>

      <app-funcionario-form 
        *ngIf="showForm"
        [funcionario]="editingFuncionario"
        (submitted)="onFormSubmitted()"
        (cancelled)="onFormCancelled()">
      </app-funcionario-form>

      <div class="table-responsive" *ngIf="!showForm">
        <table class="table table-striped">
          <thead>
            <tr>
              <th>ID</th>
              <th>Nome</th>
              <th>Cargo</th>
              <th>Data Admissão</th>
              <th>Salário</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            <tr *ngFor="let funcionario of funcionarios">
              <td>{{ funcionario.id }}</td>
              <td>{{ funcionario.nome }}</td>
              <td>{{ funcionario.cargo }}</td>
              <td>{{ funcionario.dataAdmissao | date:'dd/MM/yyyy' }}</td>
              <td>{{ funcionario.salario | currency:'BRL':'symbol':'1.2-2' }}</td>
              <td>
                <button class="btn btn-sm btn-warning" (click)="editFuncionario(funcionario)">
                  Editar
                </button>
                <button class="btn btn-sm btn-danger" (click)="deleteFuncionario(funcionario.id)">
                  Excluir
                </button>
              </td>
            </tr>
          </tbody>
        </table>

        <div class="alert alert-info" *ngIf="funcionarios.length === 0 && !loading">
          Nenhum funcionário encontrado.
        </div>
      </div>
    </div>
  </div>
</div>
